async function getPfToken() {
    const clientId = process.env.REACT_APP_PF_CLIENT_ID;
    const clientSecret = process.env.REACT_APP_PF_PASSWORD;
    try {
        const response = await axios.post(pfTokenUrl, {
            client_id: clientId,
            client_secret: clientSecret,
            audience: pfTokenAudience,
            grant_type: "client_credentials",
        });
        const accessToken = response.data.access_token;
        return accessToken;
    } catch (error) {
        console.error('Error fetching the access token:', error);
        return null;
    }
}
export async function getPfData(address, country, warmingScenario = "1.5") {
    const location = `
        country: "${country}"
        address: "${address}"
    `;

    const query = `
        mutation {
            getDatasetStatistics(input: { ${location}
                    warmingScenario: "${warmingScenario}" 
                }) {
                datasetStatisticsResponses{
                    datasetId
                    midValue
                    name
                    unit
                    warmingScenario
                    latitude
                    longitude
                    info
                }
            }
        }
    `;

    console.log(query);

    try {
        const accessToken = await getPfToken();
        const response = await fetch(pfApiUrl + "/graphql", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + accessToken,
            },
            body: JSON.stringify({ query: query, variables: {} }),
        });
        const data = await response.json();
        return JSON.stringify(data);
    } catch (error) {
        console.error('Error fetching the data:', error);
        return null;
    }
}
