import OpenAI from "openai";
import { useState, useRef, useEffect } from "react";
import { get_pf_data_schema } from "./getdataschema";
import { tools } from "./tools";
import { instructions } from "./instructions";
import UseAnimations from "react-useanimations";
import load from "react-useanimations/lib/loading";
import FeatherIcon, { arrowUp } from "feather-icons-react";
import API from "../../utils/API";

const fsPromises = require("fs").promises;
const fs = require("fs");

// import getData from "./getData";

import "./style.css";
// require('dotenv').config();

const axios = require("axios");

const openai = new OpenAI({
  apiKey: process.env.REACT_APP_OPENAI_API_KEY,
  dangerouslyAllowBrowser: true,
});

// const assistantId = process.env.ASSISTANT_ID;
// const model = "gpt-3.5-turbo";

// async function manageAssistant() {
//   try {
//     // Attempt to retrieve the assistant to check if it exists
//     const existingAssistant = await openai.retrieveAssistant({
//       assistant_id: assistantId,
//     });

//     console.log("Updating existing assistant ...");
//     // Update the assistant if it exists
//     const updatedAssistant = await openai.updateAssistant({
//       assistant_id: assistantId,
//       name: "Climate Change Assistant",
//       model,
//       // Additional properties like `instructions` and `tools` can be modified here if the API supports it
//     });
//     console.log(updatedAssistant.data);

//   } catch (error) {
//     if (error.response && error.response.status === 404) {
//       console.log("Creating assistant ...");
//       // Create a new assistant if it doesn't exist
//       const newAssistant = await openai.createAssistant({
//         name: "Climate Change Assistant",
//         model,
//         // The current API may not directly support `instructions` and `tools` in this manner; adjust accordingly
//       });
//       console.log(newAssistant.data);
//       console.log("Now save the ID in your .env file");
//     } else {
//       console.error("An error occurred:", error.message);
//     }
//   }
// }

// manageAssistant().catch(console.error);

const Assistant = () => {
  const threads = useRef();
  const [address, setAddress] = useState("");
  const [country, setCountry] = useState();

  const [response, setResponse] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);

  const [thread, setThread] = useState([]);
  const [assistantID, setAssistantID] = useState('asst_XoQZWmw7APYTRc25BARATWzB');

  let name = "Climate-Guide";
  let model = "gpt-4-1106-preview";

// Startup
  useEffect(() => {
    async function createAssistant() {
      if (assistantID !== undefined) {
        const myAssistant = await openai.beta.assistants.retrieve(assistantID);
        const assistant = await openai.beta.assistants.update(assistantID, {
          name,
          instructions,
          tools,
          model,
        });
        return console.log(assistant);
      } else {
        const assistant = await openai.beta.assistants.create({
          name,
          instructions,
          tools,
          model,
        });
        console.log(assistant.id)
        return setAssistantID(assistant.id);
      }
    }
    createAssistant();
  }, []);

  const handleSubmit = async (e) => {




    try {
      let assistantId;
      const assistantFilePath = "./assistant.json";

      // Check if the assistant.json file exists
      try {
        const myAssistant = await openai.beta.assistants.retrieve(assistantID);
        const assistant = await openai.beta.assistants.update(assistantID, {
          name,
          instructions,
          tools,
          model,
        });
        console.log("\nExisting assistant detected.\n");
        console.log(assistant);
      } catch (error) {
        // If file does not exist or there is an error in reading it, create a new assistant
        console.log("No existing assistant detected, creating new.\n");
        const assistantConfig = {
          name: "Country helper",
          instructions:
            "You're a travelling assistant, helping with information about destination countries.",
          model: "gpt-4-1106-preview",
          tools: [
            {
              type: "function",
              function: {
                name: "getCountryInformation",
                parameters: {
                  type: "object",
                  properties: {
                    country: {
                      type: "string",
                      description: "Country name, e.g. Sweden",
                    },
                  },
                  required: ["country"],
                },
                description: "Determine information about a country",
              },
            },
          ],
        };
        const assistant = await openai.beta.assistants.create(assistantConfig);
        assistantDetails = { assistantId: assistant.id, ...assistantConfig };

        // Save the assistant details to assistant.json
        await fsPromises.writeFile(
          assistantFilePath,
          JSON.stringify(assistantDetails, null, 2)
        );
        assistantId = assistantDetails.assistantId;
      }


    } catch (error) {
      console.error(error);
    }






















    e.preventDefault();
    setLoading(true);

    // thrd is Thread
    const thrd = await openai.beta.threads.create();
    // msg is Message
    const msg = await openai.beta.threads.messages.create(thrd.id, {
      role: "user",
      content: input,
    });
    const run = await openai.beta.threads.runs.create(thrd.id, {
      assistant_id: assistantID,
    });
    await checkStatus(thrd.id, run.id);

    // msg is Messages
    const msgs = await openai.beta.threads.messages.list(thrd.id);

    msgs.body.data.map((element, index, array) => {
      return setThread((thread) => [
        ...thread,
        array[array.length - index - 1],
      ]);
    });

    setInput("");
    setLoading(false);
  };

  async function checkStatus(threadId, runId) {
    let isComplete = false;


    while (!isComplete) {
      await new Promise((resolve) => setTimeout(resolve, 200));
      const runStatus = await openai.beta.threads.runs.retrieve(
        threadId,
        runId
      );

      if (runStatus.status === "requires_action") {
        let toolCalls = runStatus.required_action.submit_tool_outputs.tool_calls
        let toolOutputs = []

        for (const toolCall of toolCalls) {

          const functionName = toolCall.function.name;
          console.log(`This question requires a function: ${functionName}`);
          const args = JSON.parse(toolCall.function.arguments);
          // const argsArray = Object.keys(args).map((key)=> args[key]);
          const output = await API.getPF(args).then((res) => {
            console.log(res.data.data.getDatasetStatistics.datasetStatisticsResponses)
            // return res.data.data.getDatasetStatistics.datasetStatisticsResponses;
            return res.data;
          });
          console.log(toolOutputs)

          toolOutputs.push({
            tool_call_id: toolCall.id,
            output: output,
          })
        }
      }
      if (runStatus.status === "completed") {
        isComplete = true;
      }

      // else {
      //   await new Promise((resolve) => setTimeout(resolve, 200));
      // }
    }
  }
  useEffect(() => {
    // console.log(threads.current.scrollHeight)
    window.scrollTo({ top: threads.current.scrollHeight, behavior: "smooth" });
  }, [thread]);
  return (
    <div id="chat">
      <div className="threads" ref={threads}>
        {thread &&
          thread.map((e, i) => {
            switch (e.role) {
              case "user":
                return (
                  <div className="message user" key={i}>
                    <div className="content">
                      <p className="title">You</p>
                      <div className="text">{e.content[0].text.value}</div>
                    </div>
                  </div>
                );
              case "assistant":
                return (
                  <div className="message assistant" key={i}>
                    <div className="content">
                      <div className="title">{e.role}</div>
                      <p className="text">{e.content[0].text.value}</p>
                    </div>
                  </div>
                );
            }
          })}
      </div>

      <div className="search">
        <div className="detail"></div>
        <div className="searchbar">
          <textarea
            className="input"
            type="text"
            value={input}
            placeholder="Please ask to openai"
            onChange={(e) => setInput(e.target.value)}
          ></textarea>
        </div>
        <div className="submit">
          <button
            className="submit-btn"
            disabled={loading || input.length === 0}
            type="submit"
            onClick={handleSubmit}
          >
            {loading ? (
              <UseAnimations
                animation={load}
                strokeWidth={1}
                strokeColor="#fff"
                size={36}
                speed={0.2}
                wrapperStyle={{ padding: 0 }}
              />
            ) : (
              <FeatherIcon icon="arrow-up" size={32} strokeWidth={1.2} />
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Assistant;



